// App.js
import React, { useEffect, useState } from 'react';
import WeatherCard from './WeatherCard';
import './App.css';

const API_KEY = 'affb30227de443bbbfd25246250805';

const App = () => {
  const [location, setLocation] = useState('Pune');
  const [query, setQuery] = useState('Pune');
  const [futureWeather, setFutureWeather] = useState(null);
  const [pastWeather, setPastWeather] = useState([]);

  useEffect(() => {
    const fetchFutureWeather = async () => {
      try {
        const res = await fetch(
          `https://api.weatherapi.com/v1/forecast.json?key=${API_KEY}&q=${query}&days=5&aqi=no&alerts=no`
        );
        const data = await res.json();
        setFutureWeather(data);
      } catch (error) {
        console.error('Error fetching forecast data:', error);
      }
    };

    const fetchPastWeather = async () => {
      const results = [];
      const today = new Date();

      for (let i = 1; i <= 5; i++) {
        const pastDate = new Date(today);
        pastDate.setDate(today.getDate() - i);
        const dateString = pastDate.toISOString().split('T')[0];

        try {
          const res = await fetch(
            `https://api.weatherapi.com/v1/history.json?key=${API_KEY}&q=${query}&dt=${dateString}`
          );
          const data = await res.json();
          results.push(data);
        } catch (error) {
          console.error(`Error fetching history for ${dateString}:`, error);
        }
      }
      setPastWeather(results.reverse()); // reverse to show oldest first
    };

    fetchFutureWeather();
    fetchPastWeather();
  }, [query]);

  const handleSubmit = (e) => {
    e.preventDefault();
    setQuery(location);
  };

  return (
    <div className="app">
      <h1>Past & Future 5-Day Weather Forecast</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          placeholder="Enter city"
        />
        <button type="submit">Search</button>
      </form>

      <h2>Past 5 Days</h2>
      {pastWeather.length > 0 ? (
        <WeatherCard pastData={pastWeather} />
      ) : (
        <p>Loading past weather...</p>
      )}

      <h2>Next 5 Days</h2>
      {futureWeather && futureWeather.forecast ? (
        <WeatherCard futureData={futureWeather.forecast.forecastday} location={futureWeather.location} />
      ) : (
        <p>Loading future weather...</p>
      )}
    </div>
  );
};

export default App;
