import React, { useEffect, useState } from 'react';
import WeatherCard from './WeatherCard';
import './App.css';

const API_KEY = 'affb30227de443bbbfd25246250805'; // Replace with your actual API key

const App = () => {
  const [location, setLocation] = useState('Pune');
  const [weather, setWeather] = useState(null);
  const [query, setQuery] = useState('Pune');

  useEffect(() => {
    const fetchWeather = async () => {
      try {
        const res = await fetch(
          `https://api.weatherapi.com/v1/forecast.json?key=${API_KEY}&q=${query}&days=5&aqi=no&alerts=no`
        );
        const data = await res.json();
        setWeather(data);
      } catch (error) {
        console.error('Error fetching weather:', error);
      }
    };

    fetchWeather();
  }, [query]);

  const handleSubmit = (e) => {
    e.preventDefault();
    setQuery(location);
  };

  return (
    <div className="app">
      <h1>5 Day Weather Forecast</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          placeholder="Enter city"
        />
        <button type="submit">Search</button>
      </form>

      {weather && weather.forecast ? (
        <div className="weather-forecast">
          {weather.forecast.forecastday.map((day, index) => (
            <WeatherCard key={index} weather={day} location={weather.location} />
          ))}
        </div>
      ) : (
        <p>Loading or location not found...</p>
      )}
    </div>
  );
};

export default App;
